import {
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBModalFooter,
    MDBIcon,
    MDBCardHeader,
    MDBBtn,
    MDBInput
} from "mdbreact";
import { render } from "@testing-library/react";

export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: "default",
        };
        this.submit = this.submit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
    submit(e) {
        e.preventDefault();
        const fo = this.state.value;
        console.log(fo);

        axios.post(
            "http://localhost:8080/submitQuery",
            fo
        ).then(res => {
            console.log(res);
        }).catch(err => {
            console.log(err);
        })

        //axios.post("http://localhost:5000/abc", { headers: {'CrossDomain':true}}, fo).then((res) => { toast.success("Added")});
    }
    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    onChange = event => {
        const { name, value } = event.target;

        this.setState({ [name]: value });
    };
    render(){
        const { classes } = this.props;
        return (
            <MDBContainer>
                <MDBRow>
                    <MDBCol md="6">
                        <MDBCard>
                            <MDBCardBody>
                                <MDBCardHeader className="form-header deep-blue-gradient rounded">
                                    <h3 className="my-3">
                                        <MDBIcon icon="lock" /> Lock
                </h3>
                                </MDBCardHeader>
                                <form onSubmit={this.submit}>
                                    <div className="grey-text">
                                        <textarea
                                            name="Title"
                                            type="textarea"
                                            name="address"
                                            placeholder="ENTER SPARK SQL STATEMENT"
                                            id="oo"
                                            required
                                            onChange={this.handleChange}
                                        />
                                    </div>

                                    <div className="text-center mt-4">
                                        <MDBBtn
                                            color="light-blue"
                                            className="mb-3"
                                            type="submit"
                                        >
                                            Login
                </MDBBtn>
                                    </div>
                                </form>
                                <MDBModalFooter>
                                    <div className="font-weight-light">
                                        <p>Not a member? Sign Up</p>
                                        <p>Forgot Password?</p>
                                    </div>
                                </MDBModalFooter>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        );
    }
};
