import React, { Component } from "react";
import Navbar from "./navbar";
import "./App.css";
import userlogo from "./committee.jpg";
import TextField from "@material-ui/core/TextField";
import "bootstrap/dist/css/bootstrap.min.css";
import { makeStyles } from "@material-ui/core/styles";
import { InputAdornment, withStyles } from "@material-ui/core";
import { TextFieldsRounded, TextFieldsSharp } from "@material-ui/icons";

const styles = theme => ({
    multilineColor: {
        color: "white"
    }
});

const StyledButton = withStyles({
    root: {
        "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
            borderWidth: "2px",
            borderRadius: "20px",
            borderColor: "white"
        },
        "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
            borderWidth: "2px",
            borderRadius: "20px",
            borderColor: "red"
        },
        "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
            borderWidth: "2px",
            borderRadius: "20px",
            borderColor: "green"
        }
    }
})(TextField);

const StyledButton1 = withStyles({
    root: {
        "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
            borderWidth: "2px",
            borderRadius: "20px",
            borderColor: "white"
        },
        "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
            borderWidth: "2px",
            borderRadius: "20px",
            borderColor: "red"
        },
        "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
            borderWidth: "2px",
            borderRadius: "20px",
            borderColor: "green"
        },
        marginLeft: "145px"
    }
})(TextField);
export default class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            password: ""
        };
    }
    onChange = event => {
        const { name, value } = event.target;

        this.setState({ [name]: value });
    };
    render() {
        const { classes } = this.props;
        const { password } = this.state;

        return (
            <React.Fragment>
                <div className="main">
                    <Navbar></Navbar>
                    <title>Add Committee</title>
                    <link rel="stylesheet" type="text/css" href="App.css" />
                    <div className="companybox">
                        <img src={userlogo} className="user" />
                        <h1>Add Committee</h1>
                        <form>
                            <div>
                                <div className="container-fluid">
                                    <div className="container-fluid">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6">
                                                <StyledButton
                                                    InputProps={{
                                                        className: classes.multilineColor
                                                    }}
                                                    InputLabelProps={{
                                                        style: { color: "#fff" }
                                                    }}
                                                    id="one"
                                                    label="Username"
                                                    variant="outlined"
                                                    required
                                                />
                                            </div>
                                            <div className="col-lg-6">
                                                <PasswordInput
                                                    label="Password"
                                                    id="two"
                                                    name="password"
                                                    value={password}
                                                    onChange={this.onChange}
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div>
                                <br></br>
                                <br></br>
                                <div className="container-fluid">
                                    <div className="container-fluid">
                                        <div className="row">
                                            <div className="col-lg-6 col-md-6">
                                                <StyledButton
                                                    InputProps={{
                                                        className: classes.multilineColor
                                                    }}
                                                    InputLabelProps={{
                                                        style: { color: "#fff" }
                                                    }}
                                                    id="three"
                                                    type="email"
                                                    label="Email"
                                                    variant="outlined"
                                                    required
                                                />
                                            </div>
                                            <div className="col-lg-6">
                                                <StyledButton
                                                    InputProps={{
                                                        className: classes.multilineColor
                                                    }}
                                                    InputLabelProps={{
                                                        style: { color: "#fff" }
                                                    }}
                                                    id="four"
                                                    type="text"
                                                    label="Contact"
                                                    variant="outlined"
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <br></br>
                            <div>
                                <div className="container-fluid">
                                    <div className="container-fluid">
                                        <div className="row">
                                            <div className="col-lg-12">
                                                <br></br>
                                                <StyledButton1
                                                    InputProps={{
                                                        className: classes.multilineColor
                                                    }}
                                                    InputLabelProps={{
                                                        style: { color: "#fff" }
                                                    }}
                                                    id="five"
                                                    type="text"
                                                    label="CollegeID"
                                                    variant="outlined"
                                                    required
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <br></br>
                            <br></br>
                            <button class="button button1">Add Committee</button>
                        </form>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}
App = withStyles(styles)(App);
